import React, { useEffect, useRef } from 'react'
import { MapContainer, TileLayer, GeoJSON, useMap } from 'react-leaflet'
import L from 'leaflet'
import axios from 'axios'
import 'leaflet/dist/leaflet.css'
import { Sun, Moon, Globe2, Satellite, Trees } from 'lucide-react'

// Fix cho icon marker m·∫∑c ƒë·ªãnh c·ªßa Leaflet
delete L.Icon.Default.prototype._getIconUrl
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-shadow.png',
})

// Component ƒë·ªÉ fit bounds khi c√≥ d·ªØ li·ªáu
function FitBounds({ geojson }) {
  const map = useMap()
  
  useEffect(() => {
    if (geojson && geojson.features && geojson.features.length > 0) {
      const bounds = L.geoJSON(geojson).getBounds()
      if (bounds.isValid()) {
        map.fitBounds(bounds, { padding: [50, 50] })
      }
    }
  }, [geojson, map])
  
  return null
}

// Component ƒë·ªÉ zoom v√†o x√£ ƒë∆∞·ª£c ch·ªçn
function ZoomToFeature({ selectedXa, geojsonData }) {
  const map = useMap()
  
  useEffect(() => {
    if (selectedXa && geojsonData && geojsonData.features) {
      const feature = geojsonData.features.find(
        f => f.properties.ma_xa === selectedXa.ma_xa
      )
      if (feature && feature.geometry) {
        const bounds = L.geoJSON(feature).getBounds()
        if (bounds.isValid()) {
          map.fitBounds(bounds, { padding: [100, 100], maxZoom: 14 })
        }
      }
    }
  }, [selectedXa, geojsonData, map])
  
  return null
}

const BASE_LAYERS = {
  default: {
    key: 'default',
    label: 'M·∫∑c ƒë·ªãnh',
    icon: <Globe2 size={16} />,
    url: 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
    attribution: '&copy; OpenStreetMap contributors'
  },
  satellite: {
    key: 'satellite',
    label: 'V·ªá tinh',
    icon: <Satellite size={16} />,
    url: 'https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}',
    attribution: 'Tiles &copy; Esri'
  },
  light: {
    key: 'light',
    label: 'S√°ng',
    icon: <Sun size={16} />,
    url: 'https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png',
    attribution: '&copy; OpenStreetMap, &copy; CARTO'
  },
  dark: {
    key: 'dark',
    label: 'T·ªëi',
    icon: <Moon size={16} />,
