          <span>üß≠ Ph√¢n b·ªë d√¢n t·ªôc</span>
        </label>
        <label className="filter-checkbox">
          <input type="checkbox" checked={filters.layers.diaDiemVanHoa} onChange={() => onToggle('layers', 'diaDiemVanHoa')} />
          <span>üèõ ƒê·ªãa ƒëi·ªÉm vƒÉn h√≥a</span>
        </label>
      </div>

      <div className="filter-section">
        <div className="filter-label">Lo·∫°i ƒë·ªãa ƒëi·ªÉm vƒÉn h√≥a</div>
        {['ƒê√¨nh','Ch√πa','Nh√† vƒÉn h√≥a','ƒê·ªÅn','Mi·∫øu','Kh√°c'].map(type => (
          <label className="filter-checkbox" key={type}>
