      mouseover: (e) => {
        const layer = e.target
        layer.setStyle({
          fillOpacity: 0.8,
          weight: 2
        })
      },
      mouseout: (e) => {
        const layer = e.target
        const isSelected = selectedXa && feature.properties.ma_xa === selectedXa.ma_xa
        layer.setStyle({
          fillOpacity: isSelected ? 0.6 : 0.6,
          weight: isSelected ? 3 : 1
        })
      }
    })
  }

  // Filter features theo search query
  const filterFeatures = (data) => {
    if (!data || !data.features) return data
    if (!searchQuery) return data
    
    const filtered = {
      ...data,
      features: data.features.filter(feature => {
        const tenXa = feature.properties.ten_xa?.toLowerCase() || ''
        return tenXa.includes(searchQuery.toLowerCase())
      })
    }
    return filtered
  }

  const toggleFilter = (group, key) => {
    // group == 'phongMulti' d√πng cho danh s√°ch PGD (multi-select)
    if (group === 'phongMulti') {
      setFilters(prev => {
        const exists = prev.phong.includes(key)
        const nextPhong = exists ? prev.phong.filter(id => id !== key) : [...prev.phong, key]
        return { ...prev, phong: nextPhong }
      })
      return
    }

    setFilters(prev => ({
      ...prev,
      [group]: { ...prev[group], [key]: !prev[group][key] }
    }))
  }

  const diaDiemIcon = (loai) => {
    const colorMap = {
      'ƒê√¨nh': '#f08c00',
      'Ch√πa': '#845ef7',
      'Nh√† vƒÉn h√≥a': '#20c997',
      'ƒê·ªÅn': '#d9480f',
      'Mi·∫øu': '#2f9e44',
      'Kh√°c': '#228be6'
    }
    return L.divIcon({
      className: 'pgd-marker',
      html: `<div class="pgd-pin" style="background:${colorMap[loai] || '#228be6'}"><span class="icon-emoji">üèõÔ∏è</span></div>`
    })
  }

  const schoolIcon = (cap) => {
    const colorMap = {
      mam_non: '#ff922b',
      tieu_hoc: '#1c7ed6',
      thcs: '#845ef7',
      thpt: '#2f9e44'
    }
