import React, { useEffect, useRef } from 'react'
import { MapContainer, TileLayer, GeoJSON, useMap, Marker, Popup } from 'react-leaflet'
import L from 'leaflet'
import axios from 'axios'
import 'leaflet/dist/leaflet.css'
import { Sun, Moon, Globe2, Satellite, Trees, Filter, MapPin, Building2, School, Search } from 'lucide-react'

// Fix cho icon marker m·∫∑c ƒë·ªãnh c·ªßa Leaflet
delete L.Icon.Default.prototype._getIconUrl
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-shadow.png',
})

// Component ƒë·ªÉ fit bounds khi c√≥ d·ªØ li·ªáu
function FitBounds({ geojson }) {
  const map = useMap()
  
  useEffect(() => {
    if (geojson && geojson.features && geojson.features.length > 0) {
      const bounds = L.geoJSON(geojson).getBounds()
      if (bounds.isValid()) {
        map.fitBounds(bounds, { padding: [50, 50] })
      }
    }
  }, [geojson, map])
  
  return null
}

// Component ƒë·ªÉ zoom v√†o x√£ ƒë∆∞·ª£c ch·ªçn
function ZoomToFeature({ selectedXa, geojsonData }) {
  const map = useMap()
  
  useEffect(() => {
    if (selectedXa && geojsonData && geojsonData.features) {
      const feature = geojsonData.features.find(
        f => f.properties.ma_xa === selectedXa.ma_xa
      )
      if (feature && feature.geometry) {
        const bounds = L.geoJSON(feature).getBounds()
        if (bounds.isValid()) {
          map.fitBounds(bounds, { padding: [100, 100], maxZoom: 14 })
        }
      }
    }
  }, [selectedXa, geojsonData, map])
  
  return null
}

const BASE_LAYERS = {
  default: {
    key: 'default',
    label: 'M·∫∑c ƒë·ªãnh',
    icon: <Globe2 size={16} />,
    url: 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
    attribution: '&copy; OpenStreetMap contributors'
  },
  satellite: {
    key: 'satellite',
    label: 'V·ªá tinh',
    icon: <Satellite size={16} />,
    url: 'https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}',
    attribution: 'Tiles &copy; Esri'
  },
  light: {
    key: 'light',
    label: 'S√°ng',
    icon: <Sun size={16} />,
    url: 'https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png',
    attribution: '&copy; OpenStreetMap, &copy; CARTO'
  },
  dark: {
    key: 'dark',
    label: 'T·ªëi',
    icon: <Moon size={16} />,
    url: 'https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png',
    attribution: '&copy; OpenStreetMap, &copy; CARTO'
  },
  outdoor: {
    key: 'outdoor',
    label: 'Ngo√†i tr·ªùi',
    icon: <Trees size={16} />,
    url: 'https://tile.opentopomap.org/{z}/{x}/{y}.png',
    attribution: '&copy; OpenTopoMap'
  }
}

function BasemapToggle({ active, onChange }) {
  return (
    <div className="basemap-toggle">
      {Object.values(BASE_LAYERS).map(layer => (
        <button
          key={layer.key}
          className={`basemap-btn ${active === layer.key ? 'active' : ''}`}
          onClick={() => onChange(layer.key)}
        >
          {layer.icon}
          <span>{layer.label}</span>
        </button>
      ))}
    </div>
  )
}

function SearchBar({ searchQuery, setSearchQuery }) {
  return (
    <div className="search-box-floating">
      <Search size={18} />
      <input
        type="text"
        placeholder="T√¨m ki·∫øm x√£/ph∆∞·ªùng..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
    </div>
  )
}

function FilterPanel({ filters, onToggle, isOpen, onToggleOpen, pgdList }) {
  return (
    <div className={`filter-panel ${isOpen ? 'open' : 'closed'}`}>
      <div className="filter-header" onClick={onToggleOpen}>
        <div className="filter-title">B·ªô l·ªçc</div>
        <div className="filter-toggle">{isOpen ? '·∫®n' : 'Hi·ªán'}</div>
      </div>
      {isOpen && (
        <>
          <div className="filter-section">
            <div className="filter-label">
              <Filter size={14} /> L·ªõp d·ªØ li·ªáu
            </div>
            <label className="filter-checkbox">
              <input type="checkbox" checked={filters.layers.danToc} onChange={() => onToggle('layers', 'danToc')} />
              <span>üß≠ Ph√¢n b·ªë d√¢n t·ªôc</span>
            </label>
            <label className="filter-checkbox">
              <input type="checkbox" checked={filters.layers.diaDiemVanHoa} onChange={() => onToggle('layers', 'diaDiemVanHoa')} />
              <span>üèõ ƒê·ªãa ƒëi·ªÉm vƒÉn h√≥a</span>
            </label>
            <label className="filter-checkbox">
              <input type="checkbox" checked={filters.layers.truongHoc} onChange={() => onToggle('layers', 'truongHoc')} />
              <span>üè´ Tr∆∞·ªùng h·ªçc</span>
            </label>
          </div>

          <div className="filter-section">
            <div className="filter-label">
              <MapPin size={14} /> Lo·∫°i ƒë·ªãa ƒëi·ªÉm vƒÉn h√≥a
            </div>
            {['ƒê√¨nh','Ch√πa','Nh√† vƒÉn h√≥a','ƒê·ªÅn','Mi·∫øu','Kh√°c'].map(type => (
              <label className="filter-checkbox" key={type}>
                <input
                  type="checkbox"
                  checked={filters.loaiDiaDiem[type] ?? true}
                  onChange={() => onToggle('loaiDiaDiem', type)}
                />
                <span>{type}</span>
              </label>
            ))}
          </div>

          <div className="filter-section">
            <div className="filter-label">
              <Building2 size={14} /> Ph√≤ng
            </div>
            <select
              value={filters.phong || ''}
              onChange={(e) => onToggle('phong', e.target.value)}
              className="select-box"
            >
              <option value="">T·∫•t c·∫£</option>
              {pgdList.map(p => (
                <option key={p.id} value={p.ten}>{p.ten}</option>
              ))}
            </select>
          </div>

          <div className="filter-section">
            <div className="filter-label">
              <School size={14} /> C·∫•p h·ªçc
            </div>
            {[
              {key: 'mam_non', label: 'M·∫ßm non'},
              {key: 'tieu_hoc', label: 'Ti·ªÉu h·ªçc'},
              {key: 'thcs', label: 'THCS'},
              {key: 'thpt', label: 'THPT'},
            ].map(item => (
              <label className="filter-checkbox" key={item.key}>
                <input
                  type="checkbox"
                  checked={filters.capHoc[item.key]}
                  onChange={() => onToggle('capHoc', item.key)}
                />
                <span>{item.label}</span>
              </label>
            ))}
          </div>
        </>
      )}
    </div>
  )
}

function Map({ selectedXa, onXaSelect, searchQuery, filters, setFilters, pgdList, setPgdList }) {
  const [phuongXaData, setPhuongXaData] = React.useState(null)
  const [tinhThanhData, setTinhThanhData] = React.useState(null)
  const [loading, setLoading] = React.useState(true)
  const [error, setError] = React.useState(null)
  const [baseLayer, setBaseLayer] = React.useState('default')
  const [diaDiem, setDiaDiem] = React.useState([])
  const [truongHoc, setTruongHoc] = React.useState([])
  const [filterOpen, setFilterOpen] = React.useState(true)
  const geoJsonRef = useRef(null)

  useEffect(() => {
    // Load d·ªØ li·ªáu GeoJSON
    const loadGeoJSON = async () => {
      try {
        setLoading(true)
        const [phuongXaRes, tinhThanhRes] = await Promise.all([
          axios.get('/api/geojson/phuong-xa'),
          axios.get('/api/geojson/tinh-thanh')
        ])
        setPhuongXaData(phuongXaRes.data)
        setTinhThanhData(tinhThanhRes.data)
        setError(null)
      } catch (err) {
        console.error('L·ªói load GeoJSON:', err)
        setError('Kh√¥ng th·ªÉ t·∫£i d·ªØ li·ªáu b·∫£n ƒë·ªì')
      } finally {
        setLoading(false)
      }
    }
    
    loadGeoJSON()
  }, [])

  useEffect(() => {
    // Load ƒë·ªãa ƒëi·ªÉm vƒÉn h√≥a (t·ª´ DB)
    const loadPoints = async () => {
      try {
        const [ddvhRes, pgdRes, truongRes] = await Promise.all([
          axios.get('/api/dia-diem-van-hoa'),
          axios.get('/api/phong-giao-dich'),
          axios.get('/api/truong-hoc')
        ])
        setDiaDiem(ddvhRes.data.data || [])
        setPgdList(pgdRes.data.data || [])
        setTruongHoc(truongRes.data.data || [])
      } catch (e) {
        console.error('L·ªói load ƒë·ªãa ƒëi·ªÉm vƒÉn h√≥a:', e)
      }
    }
    loadPoints()
  }, [])

  // Style cho GeoJSON layer
  const getStyle = (feature) => {
    const isSelected = selectedXa && feature.properties.ma_xa === selectedXa.ma_xa
    
    return {
      fillColor: isSelected ? '#ff6b6b' : 'transparent',
      fillOpacity: isSelected ? 0.35 : 0,
      color: isSelected ? '#c92a2a' : '#2d3436',
      weight: isSelected ? 3 : 1,
      opacity: 0.9
    }
  }

  // X·ª≠ l√Ω khi click v√†o feature
  const onEachFeature = (feature, layer) => {
    const props = feature.properties
    const popupContent = `
      <div style="min-width: 200px;">
        <h3 style="margin: 0 0 10px 0; color: #2d3436;">${props.ten_xa || 'N/A'}</h3>
        <p style="margin: 5px 0;"><strong>Lo·∫°i:</strong> ${props.loai || 'N/A'}</p>
        <p style="margin: 5px 0;"><strong>Di·ªán t√≠ch:</strong> ${props.dtich_km2 || 0} km¬≤</p>
        <p style="margin: 5px 0;"><strong>D√¢n s·ªë:</strong> ${props.dan_so ? props.dan_so.toLocaleString('vi-VN') : 'N/A'}</p>
        <p style="margin: 5px 0;"><strong>M·∫≠t ƒë·ªô:</strong> ${props.matdo_km2 ? props.matdo_km2.toFixed(2) : 'N/A'} ng∆∞·ªùi/km¬≤</p>
        ${props.sap_nhap ? `<p style="margin: 5px 0;"><strong>S√°p nh·∫≠p:</strong> ${props.sap_nhap}</p>` : ''}
      </div>
    `
    layer.bindPopup(popupContent)
    
    layer.on({
      click: () => {
        onXaSelect({
          ma_xa: props.ma_xa,
          ten_xa: props.ten_xa,
          ...props
        })
      },
      mouseover: (e) => {
        const layer = e.target
        layer.setStyle({
          fillOpacity: 0.8,
          weight: 2
        })
      },
      mouseout: (e) => {
        const layer = e.target
        const isSelected = selectedXa && feature.properties.ma_xa === selectedXa.ma_xa
        layer.setStyle({
          fillOpacity: isSelected ? 0.6 : 0.6,
          weight: isSelected ? 3 : 1
        })
      }
    })
  }

  // Filter features theo search query
  const filterFeatures = (data) => {
    if (!data || !data.features) return data
    if (!searchQuery) return data
    
    const filtered = {
      ...data,
      features: data.features.filter(feature => {
        const tenXa = feature.properties.ten_xa?.toLowerCase() || ''
        return tenXa.includes(searchQuery.toLowerCase())
      })
    }
    return filtered
  }

  const toggleFilter = (group, key) => {
    setFilters(prev => ({
      ...prev,
      [group]: { ...prev[group], [key]: !prev[group][key] }
    }))
  }

  const diaDiemIcon = (loai) => {
    const colorMap = {
      'ƒê√¨nh': '#f08c00',
      'Ch√πa': '#845ef7',
      'Nh√† vƒÉn h√≥a': '#20c997',
      'ƒê·ªÅn': '#d9480f',
      'Mi·∫øu': '#2f9e44',
      'Kh√°c': '#228be6'
    }
    return L.divIcon({
      className: 'pgd-marker',
      html: `<div class="pgd-pin" style="background:${colorMap[loai] || '#228be6'}"><span style="font-size:11px;">VH</span></div>`
    })
  }

  if (loading) {
    return (
      <div className="map-container loading">
        <div className="loading-spinner">ƒêang t·∫£i b·∫£n ƒë·ªì...</div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="map-container error">
        <div className="error-message">{error}</div>
      </div>
    )
  }

  return (
    <div className="map-container">
      <MapContainer
        center={[10.25, 105.97]}
        zoom={10}
        style={{ height: '100%', width: '100%' }}
        zoomControl={true}
      >
        <TileLayer
          key={baseLayer}
          attribution={BASE_LAYERS[baseLayer].attribution}
          url={BASE_LAYERS[baseLayer].url}
        />
        
        {tinhThanhData && (
          <GeoJSON
            data={tinhThanhData}
            style={{
              fillOpacity: 0,
              color: '#1c7ed6',
              weight: 2.5
            }}
          />
        )}
        
        {phuongXaData && filters.layers.danToc && (
          <>
            <GeoJSON
              ref={geoJsonRef}
              data={filterFeatures(phuongXaData)}
              style={getStyle}
              onEachFeature={onEachFeature}
            />
            {!selectedXa && <FitBounds geojson={filterFeatures(phuongXaData)} />}
            <ZoomToFeature selectedXa={selectedXa} geojsonData={phuongXaData} />
          </>
        )}
        {filters.layers.diaDiemVanHoa && diaDiem
          .filter(d => filters.loaiDiaDiem[d.loai_dia_diem || 'Kh√°c'])
          .map(d => (
            <Marker
              key={`ddvh-${d.id}`}
              position={[d.vi_do, d.kinh_do]}
              icon={diaDiemIcon(d.loai_dia_diem)}
            >
              <Popup>
                <div style={{ minWidth: 200 }}>
                  <strong>{d.ten_dia_diem}</strong>
                  {d.loai_dia_diem && <div>Lo·∫°i: {d.loai_dia_diem}</div>}
                  {d.ten_xa && <div>X√£/Ph∆∞·ªùng: {d.ten_xa}</div>}
                  {d.ma_xa && <div>M√£ x√£: {d.ma_xa}</div>}
                  {d.dia_chi && <div>ƒê·ªãa ch·ªâ: {d.dia_chi}</div>}
                  {d.mo_ta && <div>M√¥ t·∫£: {d.mo_ta}</div>}
                </div>
              </Popup>
            </Marker>
          ))}

        {pgdList
          .filter(p => !filters.phong || p.ten === filters.phong)
          .map(p => (
          <Marker
            key={`pgd-${p.id}`}
            position={[p.vi_do, p.kinh_do]}
            icon={diaDiemIcon('Kh√°c')}
          >
            <Popup>
              <div style={{ minWidth: 180 }}>
                <strong>{p.ten}</strong>
              </div>
            </Popup>
          </Marker>
        ))}

        {filters.layers.truongHoc && truongHoc
          .filter(t => filters.capHoc[t.cap_hoc] || false)
          .map(t => (
            <Marker
              key={`school-${t.id}`}
              position={[t.vi_do, t.kinh_do]}
              icon={diaDiemIcon('Kh√°c')}
            >
              <Popup>
                <div style={{ minWidth: 200 }}>
                  <strong>{t.ten}</strong>
                  <div>C·∫•p: {t.cap_hoc}</div>
                  {t.ma_xa && <div>M√£ x√£: {t.ma_xa}</div>}
                  {t.ten_xa && <div>X√£/Ph∆∞·ªùng: {t.ten_xa}</div>}
                  {t.dia_chi && <div>ƒê·ªãa ch·ªâ: {t.dia_chi}</div>}
                </div>
              </Popup>
            </Marker>
          ))}

        <FilterPanel
          filters={filters}
          onToggle={toggleFilter}
          isOpen={filterOpen}
          onToggleOpen={() => setFilterOpen(!filterOpen)}
          pgdList={pgdList}
        />
        <BasemapToggle active={baseLayer} onChange={setBaseLayer} />
      </MapContainer>
      <SearchBar searchQuery={searchQuery} setSearchQuery={setFiltersSearch => setFiltersSearch} />
      <SearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
    </div>
  )
}

