import React, { useState } from 'react'
import Map from './components/Map'
import Sidebar from './components/Sidebar'
import './App.css'

function App() {
  const [selectedXa, setSelectedXa] = useState(null)
  const [searchQuery, setSearchQuery] = useState('')
  const [pgdList, setPgdList] = useState([])
  const [filters, setFilters] = useState({
    layers: { danToc: true, diaDiemVanHoa: true },
    capHoc: { mam_non: true, tieu_hoc: true, thcs: true, thpt: true },
    loaiDiaDiem: {
      'Đình': true,
      'Chùa': true,
      'Nhà văn hóa': true,
      'Đền': true,
      'Miếu': true,
      'Khác': true
    },
    phong: ''
  })

  return (
    <div className="app">
      <div className="app-container">
        <Sidebar 
          selectedXa={selectedXa}
          onXaSelect={setSelectedXa}
          searchQuery={searchQuery}
          onSearchChange={setSearchQuery}
          filters={filters}
          setFilters={setFilters}
          pgdList={pgdList}
        />
        <Map 
          selectedXa={selectedXa}
          onXaSelect={setSelectedXa}
          searchQuery={searchQuery}
          filters={filters}
          setFilters={setFilters}
          pgdList={pgdList}
        />
      </div>
    </div>
  )
}

export default App
