import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { Search, Users, MapPin, X, Info, Building2 } from 'lucide-react'
import './Sidebar.css'

function Sidebar({ selectedXa, onXaSelect, searchQuery, onSearchChange, filters, setFilters, pgdList }) {
  const [searchResults, setSearchResults] = useState([])
  const [danTocData, setDanTocData] = useState([])
  const [diaDiemData, setDiaDiemData] = useState([])
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    if (searchQuery.length > 0) {
      const timeoutId = setTimeout(() => {
        handleSearch(searchQuery)
      }, 300)
      return () => clearTimeout(timeoutId)
    } else {
      setSearchResults([])
    }
  }, [searchQuery])

  useEffect(() => {
    if (selectedXa) {
      loadDanTocData(selectedXa.ma_xa)
    }
  }, [selectedXa])

  const handleSearch = async (query) => {
    if (!query) {
      setSearchResults([])
      return
    }

    try {
      setLoading(true)
      const response = await axios.get(`/api/search?q=${encodeURIComponent(query)}`)
      setSearchResults(response.data.results || [])
    } catch (error) {
      console.error('L·ªói t√¨m ki·∫øm:', error)
    } finally {
      setLoading(false)
    }
  }

  const loadDanTocData = async (maXa) => {
    try {
      const response = await axios.get(`/api/dan-toc?ma_xa=${maXa}`)
      setDanTocData(response.data.data || [])
    } catch (error) {
      console.error('L·ªói load d√¢n t·ªôc:', error)
    }
  }

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h2>B·∫£n ƒë·ªì s·ªë Vƒ©nh Long</h2>
        <p className="subtitle">Ph√¢n b·ªë d√¢n t·ªôc & ƒê·ªãa ƒëi·ªÉm vƒÉn h√≥a</p>
      </div>

      <div className="search-section">
        <div className="search-box">
          <Search size={20} />
          <input
            type="text"
            placeholder="T√¨m ki·∫øm x√£/ph∆∞·ªùng..."
            value={searchQuery}
            onChange={(e) => onSearchChange(e.target.value)}
          />
        </div>
        
        {loading && <div className="loading-text">ƒêang t√¨m ki·∫øm...</div>}
        
        {searchResults.length > 0 && (
          <div className="search-results">
            {searchResults.map((result, index) => (
              <div 
                key={index} 
                className="search-result-item"
                onClick={() => {
                  onXaSelect(result)
                  onSearchChange('')
                }}
              >
                <div className="result-name">{result.ten_xa}</div>
                <div className="result-info">
                  {result.loai} ‚Ä¢ D√¢n s·ªë: {result.dan_so?.toLocaleString('vi-VN') || 'N/A'}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="filter-section">
        <h3>L·ªõp d·ªØ li·ªáu</h3>
        <label className="checkbox-label">
          <input
            type="checkbox"
            checked={filters.layers.danToc}
            onChange={() => setFilters(f => ({...f, layers: {...f.layers, danToc: !f.layers.danToc}}))}
          />
          <Users size={18} />
          <span>Ph√¢n b·ªë d√¢n t·ªôc</span>
        </label>
        <label className="checkbox-label">
          <input
            type="checkbox"
            checked={filters.layers.diaDiemVanHoa}
            onChange={() => setFilters(f => ({...f, layers: {...f.layers, diaDiemVanHoa: !f.layers.diaDiemVanHoa}}))}
          />
          <MapPin size={18} />
          <span>ƒê·ªãa ƒëi·ªÉm vƒÉn h√≥a</span>
        </label>
      </div>

      <div className="filter-section">
